INSERT INTO `modx_site_htmlsnippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `static`, `static_file`) VALUES
(1, 1, 0, 'tpl.AjaxForm.example', '', 0, 4, 0, '<form action="" method="post" class="ajax_form af_example form-horizontal">\n\n	<div class="control-group">\n		<label class="control-label" for="af_name">[[%af_label_name]]</label>\n		<div class="controls">\n			<input type="text" id="af_name" name="name" value="[[+fi.name]]" placeholder="" class="span4" />\n			<span class="error_name">[[+fi.error.name]]</span>\n		</div>\n	</div>\n\n	<div class="control-group">\n		<label class="control-label" for="af_email">[[%af_label_email]]</label>\n		<div class="controls">\n			<input type="email" id="af_email" name="email" value="[[+fi.email]]" placeholder="" class="span4" />\n			<span class="error_email">[[+fi.error.email]]</span>\n		</div>\n	</div>\n\n	<div class="control-group">\n		<label class="control-label" for="af_message">[[%af_label_message]]</label>\n		<div class="controls">\n			<textarea id="af_message" name="message" class="span4" rows="5">[[+fi.message]]</textarea>\n			<span class="error_message">[[+fi.error.message]]</span>\n		</div>\n	</div>\n\n	<div class="control-group">\n		<div class="controls">\n			<button type="reset" class="btn">[[%af_reset]]</button>\n			<button type="submit" class="btn btn-primary">[[%af_submit]]</button>\n		</div>\n	</div>\n	\n	[[+fi.success:is=`1`:then=`\n		<div class="alert alert-success">[[+fi.successMessage]]</div>\n	`]]\n	[[+fi.validation_error:is=`1`:then=`\n		<div class="alert alert-danger">[[+fi.validation_error_message]]</div>\n	`]]\n</form>', 0, NULL, 0, 'core/components/ajaxform/elements/chunks/chunk.example.tpl'),
(6, 0, 0, 'head', '', 0, 7, 0, '<base href="[[++site_url]]">\n    <meta charset="UTF-8">\n    <title>[[*meta_title:isempty=`[[*pagetitle]] | [[++site_name]]`]]</title>\n    <meta name="description" content="[[*meta_description:isempty=`[[*description]]`]]">\n    <meta name="keywords" content="[[*meta_keywords]]">\n    <link rel="stylesheet" href="assets/tpl/css/whhg.css">\n    <link rel="stylesheet" href="assets/tpl/css/base.css">\n    <link rel="stylesheet" href="assets/tpl/css/style.css">\n    <!--[if lte IE 8]>\n    	<script src="assets/tpl/js/html5.js"></script>\n    <![endif]-->', 0, 'a:0:{}', 0, ''),
(7, 1, 0, 'map', '', 0, 8, 0, '[[*latLng:!empty=`\n    <script type="text/javascript">\n        function initialize() {\n            var myLatLng = new google.maps.LatLng([[*latLng]]);\n            var mapOptions = {\n                zoom: 16,\n                center: myLatLng,\n                disableDefaultUI: true,\n                mapTypeIds: google.maps.MapTypeId.ROADMAP\n            }\n            var map = new google.maps.Map(document.getElementById("map"), mapOptions);\n            var marker = new google.maps.Marker({\n                position: myLatLng,\n                title:"[[*address]]"\n            });\n            marker.setMap(map);\n        }\n    </script>\n    <div id="map" style="height: 400px;"></div>\n    `]]', 0, 'a:0:{}', 0, ''),
(27, 0, 0, 'contact_form', '', 0, 8, 0, '<form class="contact_form">\n            <div class="input_wrap left">\n                <label for="name">Ваше имя: *</label>\n                <input type="text" name="name" id="name">\n            </div>\n            <div class="input_wrap right">\n                <label for="email">Ваш email: *</label>\n                <input type="email" name="email" id="email">\n            </div>\n            <div class="textarea_wrap clear">\n                <label for="message">Ваше сообщение: *</label>\n                <textarea name="message" id="message" rows="4"></textarea>\n            </div>\n            <div class="note">* - обязательные поля</div>\n            <div class="button_wrap">\n                <button type="submit" class="button">Отправить сообщение</button>\n            </div>\n        </form>', 0, 'a:0:{}', 0, ''),
(9, 1, 0, 'emailWidget', '', 0, 10, 0, '[[emailWidget]]\n<form class="widgetContactEditForm" method="post">\n    <h4>Название сайта: <span class="widgetContactEditCurrent">[[++site_name]]</span></h4>\n    <div class="widgetContactEditInput">\n        <input type="text" name="sitenameEdit" class="x-form-text x-form-field" placeholder="[[++site_name]]">\n    </div>\n    <h4>Email сайта: <span class="widgetContactEditCurrent">[[++emailsender]]</span></h4>\n    <div class="widgetContactEditInput">\n        <input type="email" name="emailsenderEdit" class="x-form-text x-form-field" placeholder="[[++emailsender]]">\n    </div>\n    <h4>Email для контактов: <span class="widgetContactEditCurrent">[[#4.email]]</span></h4>\n    <div class="widgetContactEditInput">\n        <input type="email" name="emailEdit" class="x-form-text x-form-field" placeholder="[[#4.email]]">\n    </div>\n    <h4>Телефон: <span class="widgetContactEditCurrent">[[#4.phone]]</span></h4>\n    <div class="widgetContactEditInput">\n        <input type="tel" name="phoneEdit" class="x-form-text x-form-field" placeholder="[[#4.phone]]">\n    </div>\n    <h4>Адрес: <span class="widgetContactEditCurrent">[[#4.address]]</span></h4>\n    <div class="widgetContactEditInput">\n        <input type="text" name="addressEdit" class="x-form-text x-form-field" placeholder="[[#4.address]]">\n    </div>\n    <span class="x-btn x-btn-small primary-button">\n        <button type="submit" class="x-btn-text">Изменить</button>\n    </span>\n</form>\n<style>\n    .widgetContactEditForm {\n        padding: 5px 0;\n        margin: 0 17px 0 5px;\n    }\n    .widgetContactEditCurrent {\n        display: block;\n        color: #32ab9a;\n    }\n    .widgetContactEditInput {\n        padding-bottom: 10px;\n    }\n    .widgetContactEditInput input {\n        width: 100%;\n        height: 24px;\n    }\n    .widgetContactEditInput input:focus {\n        border-color: #32ab9a;\n    }\n</style>', 0, 'a:0:{}', 0, ''),
(15, 3, 0, 'header', '', 0, 7, 0, '<header class="header middle">\n        <a href="/" class="header_logo left" title="На главную">\n            <img src="assets/tpl/images/logo.png" alt="">\n        </a>\n        <div class="header_info right">\n            <span class="header_info_tel inl rel">\n                <span class="icon-phonealt icon"></span>[[#4.phone]]\n            </span><br>\n            <a href="mailto:[[#4.email]]" class="header_info_email inl rel">\n                <span class="icon-at icon"></span>[[#4.email]]\n            </a>\n        </div>\n    </header>\n    <nav class="topmenu_wrap">\n        [[pdomenu?\n            &parents=`0`\n            &level=`1`\n            &tplOuter=`@INLINE <ul class="topmenu middle hor ct">[[+wrapper]]</ul>`\n            &tplInner=`@INLINE <ul[[+classes]]>[[+wrapper]]</ul>`\n        ]]\n    </nav>', 0, 'a:0:{}', 0, ''),
(16, 3, 0, 'scripts', '', 0, 7, 0, '<script src="assets/tpl/js/jquery-1.11.1.min.js"></script>\n    <script src="assets/tpl/js/jcarousel.js"></script>\n    <script src="assets/tpl/js/scrollto.js"></script>\n    <script src="assets/tpl/js/maskedinput.js"></script>\n    <script src="assets/tpl/js/script.js"></script>', 0, 'a:0:{}', 0, ''),
(17, 3, 0, 'footer', '', 0, 7, 0, '<footer class="footer">\n        <section class="middle">\n            <div class="footer_col">\n                <a href="" class="footer_logo inl">\n                    <img src="assets/tpl/images/logo.png" alt="">\n                </a>\n                <div class="footer_contacts">\n                    Тел.: [[#4.phone]]<br>\n                    Email: <a href="mailto:[[#4.email]]">[[#4.email]]</a>\n                </div>\n                <br>\n                <div class="footer_col_title">\n                    Мы в соц. сетях\n                </div>\n                <div class="footer_social">\n                    Блок соцсетей\n                </div>\n            </div>\n            <div class="footer_col">\n                <div class="footer_col_title">\n                    Меню\n                </div>\n                [[pdomenu?\n                    &parents=`0`\n                    &level=`1`\n                    &select=`id,pagetitle,menutitle`\n                    &tplOuter=`@INLINE <menu type="list" class="footer_list">[[+wrapper]]</menu>`\n                ]]\n            </div>\n            <div class="footer_col rt">\n                <a href="http://www.it-palitra.ru/uslugi/cool_web" target="_blank" title="Создание и продвижение сайтов" class="inl">\n                    <img src="" alt="Создание и продвижение сайтов">\n                </a>\n                <br><br>\n                <div class="footer_counters">\n                    Блок счетчиков\n                </div>\n            </div>\n        </section>\n        <section class="footer_copyright ct">\n            2015 © [[++site_name]]\n        </section>\n    </footer>', 0, 'a:0:{}', 0, ''),
(56, 2, 0, 'news_item', '', 0, 0, 0, '            <article>\n                <a href="[[~[[+id]]]]">\n                    <img src="[[+tv.pic:phpthumbof=`w=100&h=100&zc=0`]]" alt="[[+pagetitle:stripString=`"`]]" width="100" height="100">\n                </a>\n                <div>\n                    <small>[[+publishedon:dateAgo=`{"dateFormat": "d F Y"}`]]</small>\n                </div>\n                <div>\n                    <a href="[[~[[+id]]]]">[[+pagetitle]]</a>\n                </div>\n                [[+content:notags:ellipsis=`300`]]\n                <a href="[[~[[+id]]]]">читать полностью</a>\n            </article>', 0, 'a:0:{}', 0, ''),
(57, 1, 0, 'byAd', 'BannerY Ad', 0, 36, 0, '<p><a href="[[++bannery_click]]/[[+adposition]]"><img src="[[+image]]" alt="[[+name]]" title="[[+description]]"/></a></p>\n<p>Banner with extra params (available as placeholders in banner URL):<br />\n<a href="[[++bannery_click]]/[[+adposition]]?id=[[*id]]&amp;something=123"><img src="[[+image]]" alt="[[+name]]" /></a></p>\n', 0, NULL, 0, 'core/components/bannery/elements/chunks/chunk.byad.tpl'),
