plode('','', $fieldNames);\n    foreach($formFieldNames as $formFieldName){\n        list($name, $label) = explode(''=='', $formFieldName);\n        $fieldLabels[trim($name)] = trim($label);\n    }\n    foreach ($dataArray as $key => $value) {\n        if($fieldLabels[$key]){\n            $newDataArray[$fieldLabels[$key]] = $value;\n        }else{\n            $newDataArray[$key] = $value;\n        }\n    }\n    $dataArray = $newDataArray;\n}\n\n// Create obj\n$newForm = $modx->newObject(''FormItForm'');\n$newForm->fromArray(array(\n    ''form'' => $formName,\n    ''date'' => time(),\n    ''values'' => $dataArray,\n    ''ip'' => $_SERVER[''REMOTE_ADDR''],\n    ''context_key'' => $modx->resource->get(''context_key'')\n));\n$newForm->save();\n\nreturn true;', 0, NULL, '', 0, ''),
(68, 0, 0, 'getSelections', '', 0, 31, 0, '/**\n * getSelections\n *\n * DESCRIPTION\n *\n * This snippet is a helper for getResources call.\n * It will allows you to select all linked resources under specific Selection with a usage of getResources snippet.\n * Returns distinct list of linked Resources for given Selections\n *\n * getResources are required\n *\n * PROPERTIES:\n *\n * &sortdir                 string  optional    Direction of sorting by linked resource''s menuindex. Default: ASC\n * &selections              string  optional    Comma separated list of Selection IDs for which should be retrieved linked resources. Default: empty string\n * &getResourcesSnippet     string  optional    Name of getResources snippet. Default: getResources\n *\n * USAGE:\n *\n * [[getSelections? &selections=`1` &tpl=`rowTpl`]]\n * [[getSelections? &selections=`1` &tpl=`rowTpl` &sortby=`RAND()`]]\n *\n */\n\n$collections = $modx->getService(''collections'',''Collections'',$modx->getOption(''collections.core_path'',null,$modx->getOption(''core_path'').''components/collections/'').''model/collections/'',$scriptProperties);\nif (!($collections instanceof Collections)) return '''';\n\n$getResourcesSnippet = $modx->getOption(''getResourcesSnippet'', $scriptProperties, ''getResources'');\n\n$getResourcesExists = $modx->getCount(''modSnippet'', array(''name'' => $getResourcesSnippet));\nif ($getResourcesExists == 0) return ''getResources not found'';\n\n$sortDir = strtolower($modx->getOption(''sortdir'', $scriptProperties, ''asc''));\n$selections = $modx->getOption(''selections'', $scriptProperties, '''');\n$sortBy = $modx->getOption(''sortby'', $scriptProperties, '''');\n\n$selections = $modx->collections->explodeAndClean($selections);\n\nif ($sortDir != ''asc'') {\n    $sortDir = ''desc'';\n}\n\n$linkedResourcesQuery = $modx->newQuery(''CollectionSelection'');\n\nif (!empty($selections)) {\n    $linkedResourcesQuery->where(array(\n        ''collection:IN'' => $selections\n    ));\n}\n\nif ($sortBy == '''') {\n    $linkedResourcesQuery->sortby(''menuindex'', $sortDir);\n}\n\n$linkedResourcesQuery->select(array(\n    ''resource'' => ''DISTINCT(resource)''\n));\n\n$linkedResourcesQuery->prepare();\n\n$linkedResourcesQuery->stmt->execute();\n\n$linkedResources = $linkedResourcesQuery->stmt->fetchAll(PDO::FETCH_COLUMN, 0);\n$linkedResources = implode('','', $linkedResources);\n\n$properties = $scriptProperties;\nunset($properties[''selections'']);\n\n$properties[''resources''] = $linkedResources;\n$properties[''parents''] = -1;\n\nif ($sortBy == '''') {\n    $properties[''sortby''] = ''FIELD(modResource.id, '' . $linkedResources . '' )'';\n    $properties[''sortdir''] = ''asc'';\n}\n\nreturn $modx->runSnippet($getResourcesSnippet, $properties);', 0, 'a:0:{}', '', 0, '');
INSERT INTO `modx_site_snippets` (`id`, `source`, `property_preprocess`, `name`, `description`, `editor_type`, `category`, `cache_type`, `snippet`, `locked`, `properties`, `moduleguid`, `static`, `static_file`) VALUES
(74, 1, 0, 'dateAgo', '', 0, 0, 0, '/**\n * Formats date to "10 minutes ago" or "Yesterday in 22:10"\n * This algorithm taken from https://github.com/livestreet/livestreet/blob/7a6039b21c326acf03c956772325e1398801c5fe/engine/modules/viewer/plugs/function.date_format.php\n *\n * @var array $scriptProperties\n * @var string $input Date to format\n */\nif (empty($input)) {return false;}\nif (!empty($options) && $options = $modx->fromJSON($options)) {\n	$scriptProperties = array_merge($scriptProperties,$options);\n}\n\nrequire_once(MODX_CORE_PATH . ''components/dateago/include/declension.php'');\n$modx->lexicon->load(''dateago:default'');\n\n$date = preg_match(''/^\\d+$/'',$input)\n	? $input\n	: strtotime($input);\n$current = !empty($scriptProperties[''current''])\n	? $scriptProperties[''current'']\n	: time();\n$dateFormat = $scriptProperties[''dateFormat''];\n$delta = $current - $date;\n\nif (!empty($scriptProperties[''dateNow''])) {\n	if ($delta < $scriptProperties[''dateNow'']) {\n		return $modx->lexicon(''da_now'');\n	}\n}\n\nif (!empty($scriptProperties[''dateMinutes''])) {\n	$minutes = round(($delta) / 60);\n	if ($minutes < $scriptProperties[''dateMinutes'']) {\n		if ($minutes > 0) {\n			return declension($minutes, $modx->lexicon(''da_minutes'',array(''minutes'' => $minutes)));\n		}\n		else {\n			return $modx->lexicon(''da_minutes_less'');\n		}\n	}\n}\n\nif (!empty($scriptProperties[''dateHours''])) {\n	$hours = round(($delta) / 3600);\n	if ($hours < $scriptProperties[''dateHours'']) {\n		if ($hours > 0) {\n			return declension($hours, $modx->lexicon(''da_hours'',array(''hours'' => $hours)));\n		}\n		else {\n			return $modx->lexicon(''da_hours_less'');\n		}\n	}\n}\n\nif (!empty($scriptProperties[''dateDay''])) {\n	switch(date(''Y-m-d'', $date)) {\n		case date(''Y-m-d''):\n			$day = $modx->lexicon(''da_today'');\n			break;\n		case date(''Y-m-d'', mktime(0, 0, 0, date(''m'')  , date(''d'')-1, date(''Y'')) ):\n			$day = $modx->lexicon(''da_yesterday'');\n			break;\n		case date(''Y-m-d'', mktime(0, 0, 0, date(''m'')  , date(''d'')+1, date(''Y'')) ):\n			$day = $modx->lexicon(''da_tomorrow'');\n			break;\n		default:\n			$day = null;\n	}\n	if($day) {\n		$format = str_replace("day",preg_replace("#(\\w{1})#",''\\\\\\${1}'',$day),$scriptProperties[''dateDay'']);\n		return date($format,$date);\n	}\n}\n\n$month_arr = $modx->fromJSON($modx->lexicon(''da_months''));\n$m = date("n", $date);\n$month = $month_arr[$m - 1];\n\n$format = preg_replace("~(?<!\\\\\\\\)F~U", preg_replace(''~(\\w{1})~u'',''\\\\\\${1}'',$month), $dateFormat);\n\nreturn date($format ,$date);', 0, 'a:6:{s:5:"input";a:7:{s:4:"name";s:5:"input";s:4:"desc";s:8:"da_input";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}s:10:"dateFormat";a:7:{s:4:"name";s:10:"dateFormat";s:4:"desc";s:13:"da_dateFormat";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:10:"d F Y, H:i";s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}s:7:"dateNow";a:7:{s:4:"name";s:7:"dateNow";s:4:"desc";s:10:"da_dateNow";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:10;s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}s:7:"dateDay";a:7:{s:4:"name";s:7:"dateDay";s:4:"desc";s:10:"da_dateDay";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:7:"day H:i";s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}s:11:"dateMinutes";a:7:{s:4:"name";s:11:"dateMinutes";s:4:"desc";s:14:"da_dateMinutes";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";s:2:"59";s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}s:9:"dateHours";a:7:{s:4:"name";s:9:"dateHours";s:4:"desc";s:12:"da_dateHours";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";s:2:"10";s:7:"lexicon";s:18:"dateago:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/dateago/elements/snippets/snippet.dateago.php'),
(75, 1, 0, 'BannerY', 'Show ads on your site', 0, 36, 0, '/** @var array $scriptProperties */\n/* @var pdoFetch $pdoFetch */\nif (!$modx->getService(''pdoFetch'')) {return ''You need to install pdoTools to use this snippet!'';}\n$pdoFetch = new pdoFetch($modx, $scriptProperties);\n$pdoFetch->addTime(''pdoTools loaded'');\n$modx->lexicon->load(''bannery:default'');\n$modx->addPackage(''bannery'', MODX_CORE_PATH . ''components/bannery/model/'');\n\nif (!empty($tplOuter)) {$tplWrapper = $tplOuter;}\nif (empty($outputSeparator)) {$outputSeparator = "\\n";}\n$class = ''byAd'';\n\n// Adding extra parameters into special place so we can put them in results\n/** @var modSnippet $snippet */\n$additionalPlaceholders = array();\nif ($snippet = $modx->getObject(''modSnippet'', array(''name'' => ''BannerY''))) {\n	$properties = unserialize($snippet->properties);\n	foreach ($scriptProperties as $k => $v) {\n		if (!isset($properties[$k])) {\n			$additionalPlaceholders[$k] = $v;\n		}\n	}\n}\n// ---\n\n$date = date(''Y-m-d H:i:s'');\n$where = array(\n	"({$class}.start IS NULL OR {$class}.start <= ''{$date}'') AND ({$class}.end IS NULL OR {$class}.end >= ''{$date}'')"\n);\nif (empty($showInactive)) {\n	$where[$class.''.active''] = 1;\n}\nif (!empty($position)) {\n	$where[''byAdPosition.position''] = $position;\n}\nelseif (!empty($positions)) {\n	$where[''byAdPosition.position:IN''] = array_map(''trim'', explode('','', $positions));\n}\n\nif (empty($sortby)) {\n	$sortby = ''RAND()'';\n}\nelseif ($sortby == ''idx'' || $sortby == ''index'') {\n	$sortby = ''byAdPosition.idx'';\n}\nelse {\n	$sortby = $class.''.''.$sortby;\n}\n\n$innerJoin = array(\n	''byAdPosition'' => array(\n		''alias'' => ''byAdPosition'',\n		''on'' => $class.''.id = byAdPosition.ad''\n	)\n);\n\n// Fields to select\n$select = array(\n	$class => ''all'',\n	''byAdPosition'' => ''`byAdPosition`.`id` as `adposition`, `byAdPosition`.`ad`''\n);\n\n// Add custom parameters\nforeach (array(''where'',''innerJoin'',''select'') as $v) {\n	if (!empty($scriptProperties[$v])) {\n		$tmp = $modx->fromJSON($scriptProperties[$v]);\n		if (is_array($tmp)) {\n			$$v = array_merge($$v, $tmp);\n		}\n	}\n	unset($scriptProperties[$v]);\n}\n$pdoFetch->addTime(''Conditions prepared'');\n\n$default = array(\n	''class'' => $class,\n	''innerJoin'' => $modx->toJSON($innerJoin),\n	''where'' => $modx->toJSON($where),\n	''select'' => $modx->toJSON($select),\n	''sortby'' => $sortby,\n	''groupby'' => $class.''.id'',\n	''return'' => ''data'',\n	''disableConditions'' => true,\n);\n\n$pdoFetch->setConfig(array_merge($default, $scriptProperties));\n$rows = $pdoFetch->run();\n\n$output = array();\n$default_source = $modx->getOption(''bannery.media_source'', null, $modx->getOption(''default_media_source''));\n$sources = array();\nforeach ($rows as $row) {\n	$source = !empty($row[''source''])\n		? $row[''source'']\n		: $default_source;\n\n	if (!isset($sources[$row[''source'']])) {\n		/** @var modMediaSource $source */\n		if ($source = $modx->getObject(''sources.modMediaSource'', $source)) {\n			$source->initialize($modx->context->key);\n		}\n		$sources[$row[''source'']] = $source;\n	}\n	else {\n		$source = $sources[$row[''source'']];\n	}\n\n	if (!empty($source) && $source instanceof modMediaSource && !empty($row[''image''])) {\n		$row[''image''] = $source->getObjectUrl($row[''image'']);\n	}\n\n	$row[''idx''] = $pdoFetch->idx++;\n	$tpl = $pdoFetch->defineChunk($row);\n	if (!empty($additionalPlaceholders)) {\n		$row = array_merge($additionalPlaceholders, $row);\n	}\n\n	$output[] = !empty($tpl)\n		? $pdoFetch->getChunk($tpl, $row, $pdoFetch->config[''fastMode''])\n		: ''<pre>''.$pdoFetch->getChunk('''', $row).''</pre>'';\n}\n\nif ($modx->user->hasSessionContext(''mgr'') && !empty($showLog)) {\n	$output[''log''] = ''<pre class="pdoUsersLog">'' . print_r($pdoFetch->getTime(), 1) . ''</pre>'';\n}\n\n// Return output\nif (!empty($toSeparatePlaceholders)) {\n	$modx->setPlaceholders($output, $toSeparatePlaceholders);\n}\nelse {\n	$output = implode($outputSeparator, $output);\n\n	if (!empty($tplWrapper) && (!empty($wrapIfEmpty) || !empty($output))) {\n		$output = $pdoFetch->getChunk($tplWrapper, array(''output'' => $output), $pdoFetch->config[''fastMode'']);\n	}\n\n	if (!empty($toPlaceholder)) {\n		$modx->setPlaceholder($toPlaceholder, $output);\n	}\n	else {\n		return $output;\n	}\n}', 0, 'a:18:{s:9:"positions";a:7:{s:4:"name";s:9:"positions";s:4:"desc";s:22:"bannery_prop_positions";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:7:"showLog";a:7:{s:4:"name";s:7:"showLog";s:4:"desc";s:20:"bannery_prop_showLog";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:8:"fastMode";a:7:{s:4:"name";s:8:"fastMode";s:4:"desc";s:21:"bannery_prop_fastMode";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:5:"limit";a:7:{s:4:"name";s:5:"limit";s:4:"desc";s:18:"bannery_prop_limit";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:5;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:6:"offset";a:7:{s:4:"name";s:6:"offset";s:4:"desc";s:19:"bannery_prop_offset";s:4:"type";s:11:"numberfield";s:7:"options";a:0:{}s:5:"value";i:0;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:6:"sortby";a:7:{s:4:"name";s:6:"sortby";s:4:"desc";s:19:"bannery_prop_sortby";s:4:"type";s:4:"list";s:7:"options";a:7:{i:0;a:2:{s:4:"text";s:6:"Random";s:5:"value";s:6:"RAND()";}i:1;a:2:{s:4:"text";s:5:"Index";s:5:"value";s:3:"idx";}i:2;a:2:{s:4:"text";s:4:"Name";s:5:"value";s:4:"name";}i:3;a:2:{s:4:"text";s:3:"Url";s:5:"value";s:3:"url";}i:4;a:2:{s:4:"text";s:5:"Image";s:5:"value";s:5:"image";}i:5;a:2:{s:4:"text";s:6:"Active";s:5:"value";s:6:"active";}i:6;a:2:{s:4:"text";s:11:"Description";s:5:"value";s:11:"description";}}s:5:"value";s:6:"RAND()";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:7:"sortdir";a:7:{s:4:"name";s:7:"sortdir";s:4:"desc";s:20:"bannery_prop_sortdir";s:4:"type";s:4:"list";s:7:"options";a:2:{i:0;a:2:{s:4:"text";s:3:"ASC";s:5:"value";s:3:"ASC";}i:1;a:2:{s:4:"text";s:4:"DESC";s:5:"value";s:4:"DESC";}}s:5:"value";s:3:"ASC";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:15:"outputSeparator";a:7:{s:4:"name";s:15:"outputSeparator";s:4:"desc";s:28:"bannery_prop_outputSeparator";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:1:"\n";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:5:"where";a:7:{s:4:"name";s:5:"where";s:4:"desc";s:18:"bannery_prop_where";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:12:"showInactive";a:7:{s:4:"name";s:12:"showInactive";s:4:"desc";s:25:"bannery_prop_showInactive";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:16:"bannery_prop_tpl";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:4:"byAd";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:8:"tplFirst";a:7:{s:4:"name";s:8:"tplFirst";s:4:"desc";s:21:"bannery_prop_tplFirst";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:7:"tplLast";a:7:{s:4:"name";s:7:"tplLast";s:4:"desc";s:20:"bannery_prop_tplLast";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:6:"tplOdd";a:7:{s:4:"name";s:6:"tplOdd";s:4:"desc";s:19:"bannery_prop_tplOdd";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:10:"tplWrapper";a:7:{s:4:"name";s:10:"tplWrapper";s:4:"desc";s:23:"bannery_prop_tplWrapper";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:11:"wrapIfEmpty";a:7:{s:4:"name";s:11:"wrapIfEmpty";s:4:"desc";s:24:"bannery_prop_wrapIfEmpty";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:13:"toPlaceholder";a:7:{s:4:"name";s:13:"toPlaceholder";s:4:"desc";s:26:"bannery_prop_toPlaceholder";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}s:22:"toSeparatePlaceholders";a:7:{s:4:"name";s:22:"toSeparatePlaceholders";s:4:"desc";s:35:"bannery_prop_toSeparatePlaceholders";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"bannery:properties";s:4:"area";s:0:"";}}', '', 0, 'core/components/bannery/elements/snippets/snippet.bannery.php'),
(76, 0, 0, 'numformat', '', 0, 9, 0, '/*\n * numFormat snippet\n * example: [[*price:numformat]]\n */\n\nif(strlen($input)==0) return '''';\n\n$input = floatval(str_replace(array('' '','',''), array('''',''.''), $input));\nreturn number_format($input,(floor($input) == $input ? 0 : 2),''.'','' '');', 0, NULL, '', 0, '');
