(1, 0, 0, 'Главная страница', 'Template', 0, 0, '', 0, '<!DOCTYPE html>\n<html lang="ru">\n<head>\n    [[$head]]\n</head>\n<body>\n    [[$header]]\n    [[$slider]]\n    [[$footer]]\n    [[$scripts]]\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
(2, 0, 0, 'Стандартный', '', 0, 0, '', 0, '<!DOCTYPE html>\n<html lang="ru">\n<head>\n    [[$head]]\n</head>\n<body>\n    [[$header]]\n    <section class="main middle">\n        [[pdocrumbs? &showHome=`1`]]\n        [[*parent:is=`3`:then=`[[*publishedon:dateAgo=`{"dateFormat": "d F Y"}`]]`]]\n        <div class="title">[[*longtitle:isempty=`[[*pagetitle]]`]]</div>\n        [[*pic:!empty=`<img src="[[*pic:phpthumbof=`w=200&h=200&zc=1`]]" alt="[[*pagetitle]]" width="200">`]]\n[[*content]]\n        [[*parent:is=`3`:then=`[[pdoneighbors]]`]]\n    </section>\n    [[$footer]]\n    [[$scripts]]\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
(3, 0, 0, 'Контакты', '', 0, 0, '', 0, '<!DOCTYPE html>\n<html lang="ru">\n<head>\n    [[$head]]\n    <script src="http://maps.googleapis.com/maps/api/js?key=AIzaSyBD0wF5dYryu4S5QG08BbYPPqm-0hWl7xw&sensor=false"></script>\n</head>\n<body[[*latLng:!empty=` onload="initialize()"`]]>\n[[$header]]\n<section class="main middle">\n    <div class="title">[[*longtitle:isempty=`[[*pagetitle]]`]]</div>\n    <div class="main_half left">\n        [[*content:!empty=`\n        <div class="contact_text">\n            [[*content]]\n        </div>\n        `]]\n        [[*phone:!empty=`<span class="bold">Тел.:</span> [[*phone]]<br>`]]\n        [[*email:!empty=`<span class="bold">Email:</span> <a href="mailto:[[*email]]">[[*email]]</a><br>`]]\n        [[*address:!empty=`<span class="bold">Адрес:</span> [[*address]]`]]\n    </div>\n    <div class="main_half right">\n        <div class="subtitle">Напишите нам:</div>\n        [[!AjaxForm?\n            &snippet=`FormIt`\n            &form=`contact_form`\n            &hooks=`email`\n            &emailSubject=`Тестовое сообщение`\n            &emailTo=`[[*email]]`\n            &validate=`name:required,email:email:required,message:stripTags:required`\n            &validationErrorMessage=`В форме содержатся ошибки!`\n            &successMessage=`Сообщение успешно отправлено`\n        ]]\n    </div>\n</section>\n<section class="middle">\n    [[$map]]\n</section>\n[[$footer]]\n[[$scripts]]\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
(7, 2, 0, 'Каталог', '', 0, 37, '', 0, '<!DOCTYPE html>\n<html lang="ru">\n<head>\n    [[$head]]\n</head>\n<body>\n    [[$header]]\n    <section class="main middle">\n        [[pdocrumbs? &showHome=`1`]]\n        <div class="title">[[*longtitle:isempty=`[[*pagetitle]]`]]</div>\n        <div id="pagination_wrapper">\n            <div id="pagination_rows">\n[[!pdopage?\n    &select=`id,pagetitle`\n    &includeTVs=`pic`\n    &processTVs=`1`\n    &depth=`0`\n    &sortby=`menuindex`\n    &sortdir=`desc`\n    &tpl=`category_item`\n    &limit=`2`\n    &pageLimit=`10`\n    &tplPage=`@INLINE <li><a href="[[+href]]">[[+pageNo]]</a></li>`\n    &tplPageWrapper=`@INLINE <nav id="pagination_list"><ul class="pagination hor">[[+first]][[+prev]][[+pages]][[+next]][[+last]]</ul></nav>`\n    &ajaxMode=`default`\n    &ajaxElemWrapper=`#pagination_wrapper`\n    &ajaxElemRows=`#pagination_rows`\n    &ajaxElemPagination=`#pagination_list`\n    &ajaxElemLink=`#pagination_list a`\n]]\n            </div>\n            [[!+page.nav]]\n        </div>\n    </section>\n    [[$footer]]\n    [[$scripts]]\n</body>\n</html>', 0, 'a:0:{}', 0, ''),
